package Task_25_4;

public class Multiple2To10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

        int number = 12; 
        
        if (number % 2 == 0) 
        {
            System.out.println(number + " is a multiple of 2.");
        } 
        else if (number % 3 == 0) 
        {
            System.out.println(number + " is a multiple of 3.");
        } 
        else if (number % 4 == 0) 
        {
            System.out.println(number + " is a multiple of 4.");
        } 
        else if (number % 5 == 0)
        {
            System.out.println(number + " is a multiple of 5.");
        } 
        else if (number % 6 == 0)
        {
            System.out.println(number + " is a multiple of 6.");
        } 
        else if (number % 7 == 0) 
        {
            System.out.println(number + " is a multiple of 7.");
        } 
        else if (number % 8 == 0) 
        {
            System.out.println(number + " is a multiple of 8.");
        } 
        else if (number % 9 == 0)
        {
            System.out.println(number + " is a multiple of 9.");
        } 
        else if (number % 10 == 0) 
        {
            System.out.println(number + " is a multiple of 10.");
        }
        else 
        {
            System.out.println(number + " is not a multiple of 2 to 10.");
        }
    }
}
	
		
		
	


