package Task_26_4;

public class Multiple {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

        int i = 1;
	   // Printing multiples of 2, 3, 4, and 5
	   System.out.println("Multiples of 2, 3, 4, and 5:");
	   while (i <= 10) 
	   {
	   System.out.println("2 x " + i + " = " + (2 * i) + "\t3 x " + i + " = " + (3 * i) + "\t4 x " + i + " = " + (4 * i) + "\t5 x " + i + " = " + (5 * i));
	   i++;
	   }
				        
	}

}
