package Task_26_4;

public class Reversing_Numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Reversing numbers from 10 to 1
        System.out.println("Reversed numbers from 10 to 1:");
        int i = 10;
        while (i >= 1) 
        {
            System.out.println(i);
            i--;
        }
	}
}
