package Task_26_4;

public class Whileloop1to30numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("Numbers from 1 to 30:");
        int i = 1;
        while (i <= 30) 
        {
            System.out.println(i);
            i++;
        }
	}

}
